	      if(self.s.options.j != null) ops.j = self.s.options.j;
	      if(self.s.options.fsync != null) ops.fsync = self.s.options.fsync;
	      if(self.s.options.wtimeout != null) ops.wtimeout = self.s.options.wtimeout;
